
$(document).ready(function(){$('.gallery-set__slider').slick({arrows:false,slide:'.gallery-set__image',slidesToShow:1,slidesToScroll:1,infinite:false,fade:true,speed:700});$('.gallery-set__thumbnails').slick({slide:'.gallery-set__thumbnail',slidesToShow:4,slidesToScroll:1,asNavFor:'.gallery-set__slider',arrows:false,variableWidth:true,infinite:false,focusOnSelect:true});$(window).resize(function(){$('.gallery-set__thumbnails').slick('reinit');});$(window).on('orientationchange',function(){$('.gallery-set__thumbnails').slick('reinit');});});