
(function(){var navOpen=document.querySelector(".main-header__nav-open");var mobLinks=document.querySelector(".main-header__links-wrap");navOpen.addEventListener("click",function(event){event.preventDefault();mobLinks.classList.toggle("main-header__links-wrap_closed");mobLinks.classList.toggle("main-header__links-wrap_opened");navOpen.classList.toggle("main-header__nav-open_closed");navOpen.classList.toggle("main-header__nav-open_opened");});window.addEventListener("keydown",function(event){if(event.keyCode==27&&mobLinks.classList.contains("main-header__links-wrap_opened")){mobLinks.classList.remove("main-header__links-wrap_opened");mobLinks.classList.add("main-header__links-wrap_closed");navOpen.classList.remove("main-header__nav-open_opened");navOpen.classList.add("main-header__nav-open_closed");}});})();