
(function(){var filterOpen=document.querySelector(".rating-full__filter-open");var filterClose=document.querySelector(".rating-filter__filter-close");var sidebar=document.querySelector(".rating-full__sidebar");var sidebarActiveClass="rating-full__sidebar_show-mob";var starsBlock=document.querySelector(".rating-filter__stars-wrap");var stars=document.querySelectorAll(".rating-filter__star");var starActiveClass="rating-filter__star_active";var starsInput=document.querySelector(".rating-filter__stars-number");var starsCounter=Number(starsInput.value);var resetBtn=document.querySelector(".rating-filter__btn");var initStars=function(){if(starsCounter>0){for(var i=1;i<=starsCounter;i++){stars[i-1].classList.add(starActiveClass);}}};var clearStars=function(){for(var i=0;i<stars.length;i++){stars[i].classList.remove(starActiveClass);}};initStars();starsBlock.addEventListener('click',function(event){event.preventDefault();if(starsCounter===5){starsCounter=0;starsInput.value=0;clearStars();}else{starsCounter++;starsInput.value=starsCounter;stars[starsCounter-1].classList.add(starActiveClass);}});resetBtn.addEventListener('click',function(event){starsCounter=0;starsInput.value=0;clearStars();});filterOpen.addEventListener('click',function(event){event.preventDefault();sidebar.classList.add(sidebarActiveClass);});filterClose.addEventListener('click',function(event){event.preventDefault();sidebar.classList.remove(sidebarActiveClass);});})();