
(function(){window.toggleBlock=function(navElement,collapsedBlock,activeNavClass,inactiveNavClass,activeBlockClass){navElement.addEventListener('click',function(event){event.preventDefault();navElement.classList.toggle(activeNavClass);navElement.classList.toggle(inactiveNavClass);if(navElement.classList.contains(activeNavClass)){collapsedBlock.classList.add(activeBlockClass);}else{collapsedBlock.classList.remove(activeBlockClass);}});};window.toggleBlockPlain=function(navElement,collapsedBlock,activeNavClass,activeBlockClass){navElement.addEventListener('click',function(event){event.preventDefault();navElement.classList.toggle(activeNavClass);if(navElement.classList.contains(activeNavClass)){collapsedBlock.classList.add(activeBlockClass);}else{collapsedBlock.classList.remove(activeBlockClass);}});};})();