
(function(){var videoItems=document.querySelectorAll(".video__item");var videoActiveClass="video__item_selected";var selectBlock=function(block,blockNumber,blocks,activeBlockClass){block.addEventListener('click',function(event){if((window.matchMedia("(min-width: 764px)").matches)&&!block.classList.contains(activeBlockClass)){event.preventDefault();event.stopPropagation();for(var j=0;j<blocks.length;j++){if(j===blockNumber){blocks[j].classList.add(activeBlockClass);}else{blocks[j].classList.remove(activeBlockClass);}}}});};for(var i=0;i<videoItems.length;i++){selectBlock(videoItems[i],i,videoItems,videoActiveClass);}})();